import Dexie from 'dexie';

import { setTheme } from '@/db/session_db'

const db = new Dexie('qrSc4nner-LocalDatabase');

db.version(1).stores({
    settings: 'id, theme'
});

init()

export async function init() {
    const settings_exist = (await db.settings.toArray()).length
    if (!settings_exist) {
        db.settings.add({'id': 0, 'theme': 'theme-dark'})
    }
}

export async function getTheme() {
    const curr_theme = (await db.settings.toArray())[0].theme
    setTheme(curr_theme)
}
