import Dexie from 'dexie';

const db = new Dexie('qrSc4nner-LocalDatabase');

db.version(1).stores({
    settings: 'id, theme'
});

export async function init() {
    const settings_exist = (await db.settings.toArray()).length
    if (!settings_exist) {
        db.settings.add({'id': 0, 'theme': 'theme-dark'})
    }
}
